function makePyramid(e,r){for(i=1;i<=e;i++){const n=document.createElement("div");for(c=1;c<=e-i;c++)n.innerHTML+="&nbsp;";for(j=1;j<=i;j++)n.innerHTML+=r+"&nbsp;";document.querySelector("body").appendChild(n)}document.querySelector("body").innerHTML+="<hr>"}function makeMultiCharPyramid(e,r){for(i=1;i<=e;i++){const n=document.createElement("div");for(c=1;c<=e-i;c++)n.innerHTML+="&nbsp;&nbsp;";for(j=1;j<=i;j++)n.innerHTML+="&nbsp;"+r;document.querySelector("body").appendChild(n)}document.querySelector("body").innerHTML+="<hr>"}makePyramid(10,":"),makePyramid(3,"*"),makePyramid(5,"^"),makeMultiCharPyramid(5,"^-^"),makeMultiCharPyramid(8,"qwert"),makeMultiCharPyramid(10,"1 2 3");